/** @format */

import React from 'react';
import '../App.css';
import ExpContent from '../components/ExpContent';
import ExpBlock from '../components/ExpBlock';
import { ExpItems } from '../TextInfo';

const Experience = () => {
	return (
		<div className='container_pages container_exp'>
			{/* <h1 className='title_exp'>Working Experience</h1> */}
			<div className='exp_li'>
				{ExpItems.map((item, index) => {
					return item.cert != null ? (
						<span key={index}>
							<ExpContent i={index} />
						</span>
					) : (
						<span key={index}>
							<ExpBlock i={index} />
						</span>
					);
				})}
			</div>
		</div>
	);
};

export default Experience;
