/** @format */

import React, { useState } from 'react';
import '../App.css';
import { Link } from 'react-router-dom';
import { ExpItems } from '../TextInfo';
import { MdArrowRight, MdArrowDropDown } from 'react-icons/md';

const Experience = () => {
	const [active, setActive] = useState(false);
	return (
		<div className='container_pages container_exp'>
			{/* <h1 className='title_exp'>Working Experience</h1> */}
			<div className='exp_li'>
				{ExpItems.map((item, index) => {
					const background = {
						backgroundImage: `url(../images/loc/${item.img})`,
					};
					return item.cert != null ? (
						<span>
							<span
								className='experience'
								key={index}
								onClick={(e) => setActive(!active)}>
								<div className='loc-img' style={background}></div>
								<span className='exp_text'>
									<span className='cert_work_title'>
										<h3 className='work_title'>{item.title}</h3>
										<span className='toggle'>
											<MdArrowDropDown
												className='toggle-arr'
												onClick={showMenu}
											/>
										</span>
									</span>
									<a target='_blank' href={item.link}>
										{item.loc}
									</a>
									<p className='work_period'>{item.period}</p>
								</span>
							</span>
							{active && (
								<span className='exp_content'>
									{item.cert.map((item, index) => {
										const certLink = `/../images/cert/${item}`;
										const cert = {
											backgroundImage: `url(${certLink})`,
										};
										return (
											<Link to={certLink}>
												<div className='exp_cert' style={cert}></div>
											</Link>
										);
									})}
								</span>
							)}
						</span>
					) : (
						<span className='experience' key={index}>
							<div className='loc-img' style={background}></div>
							<span className='exp_text'>
								<h3 className='work_title'>{item.title}</h3>
								<a target='_blank' href={item.link}>
									{item.loc}
								</a>
								<p className='work_period'>{item.period}</p>
							</span>
						</span>
					);
				})}
			</div>
		</div>
	);
};

export default Experience;
