import { React, useState } from 'react';
import { MenuList } from '../TextInfo';
import '../App.css';
import Swipe from '../components/Swipe';
import { MdArrowRight, MdArrowDropDown } from 'react-icons/md';
import HorizontalScroll from '../components/HorizontalScroll';

const Events = () => {
    const [active, setActive] = useState( 1000 );
    const dropdown = ( index ) => {
        if ( active !== index )
            setActive( index );
        else
            setActive( 1000 );
    };
    return (
        <span className="container_pages container_exp">
            <span className="menu_list">
                {MenuList.map( ( item, index ) => {
                    return <span className="menu_cat">
                        <span className='exp_block exp_cert_block' onClick={() => dropdown( index )} key={index}>

                            <span className='exp_header'>
                                <span className='exp_toggle_title'>
                                    <h3 className='exp_position'>{item.category}</h3>
                                    <span className='toggle_icon'>
                                        {active === index ? (
                                            <MdArrowDropDown className='toggle-arr' />
                                        ) : (
                                            <MdArrowRight className='toggle-arr' />
                                        )}
                                    </span>
                                </span>
                            </span>
                        </span>
                        {active === index && (
                            item.isSet ? (
                                // item.set.map( ( dish, index ) => {
                                //     return <span className="menu_set">
                                //         <h3>{dish.name}</h3>
                                //         {dish.desc.map( ( dishDesc, index ) => {
                                //             const dishImg = {
                                //                 backgroundImage: `url(../images/menus/cafe-des.png)`,
                                //             };
                                //             return <span className="menu_dish">
                                //                 <div className="menu_dish_img" style={dishImg}></div>
                                //                 <p>{dishDesc}</p>
                                //             </span>
                                //         } )}
                                //     </span>
                                // } )
                                null
                            ) : (
                                item.set.map( ( dish, index ) => {
                                    const dishImg = {
                                        backgroundImage: `url(../images/menus/cafe-des.png)`,
                                    };
                                    return <span className="menu_dish">
                                        <div className="menu_dish_img" style={dishImg}></div>
                                        <h5>{dish.name}</h5>
                                        {dish.desc != null ?
                                            dish.desc.map( ( dishDesc, index ) => {
                                                return <p className="menu_dish_ing">{dishDesc}</p>
                                            } )
                                            : null}
                                    </span>
                                } )
                            )
                        )}
                    </span>
                } )}
            </span>
        </span>
    );
}
export default Events
