/** @format */

import React, { useState } from 'react';
import '../App.css';
import { ExpItems } from '../TextInfo';
import { MdArrowRight, MdArrowDropDown } from 'react-icons/md';

const ExpContent = (i) => {
	const [active, setActive] = useState(false);
	const dropdown = () => {
		setActive(!active);
		console.log(document.getElementsByClassName('toggle').innerHTML);
	};

	i = i.i;
	const item = ExpItems[i];
	const background = {
		backgroundImage: `url(../images/loc/${item.img})`,
	};
	return (
		<>
			<span className='experience exp_cert_block' onClick={dropdown}>
				<div className='loc-img' style={background}></div>
				<span className='exp_text'>
					<span className='cert_work_title'>
						<h3 className='work_title'>{item.title}</h3>
						<span className='toggle'>
							{active ? (
								<MdArrowDropDown className='toggle-arr' />
							) : (
								<MdArrowRight className='toggle-arr' />
							)}
						</span>
					</span>
					<a target='_blank' rel='noreferrer' href={item.link}>
						{item.loc}
					</a>
					<p className='work_period'>{item.period}</p>
				</span>
			</span>
			{active && (
				<span className='exp_content'>
					{item.cert.map((item, index) => {
						const certLink = `/../images/certHD/${item}`;
						const cert = {
							backgroundImage: `url(/../images/cert/${item})`,
						};
						return (
							<a key={index} target='_blank' rel='noreferrer' href={certLink}>
								{item.loc}
								<div className='exp_cert' style={cert}></div>
							</a>
						);
					})}
				</span>
			)}
		</>
	);
};

export default ExpContent;
