/** @format */

import React, { useState } from 'react';
import '../App.css';
import { ExpItems } from '../TextInfo';

const Experience = () => {
	const [active, setActive] = useState(false);
	const showMenu = () => {
		setActive(!active);
	};
	return (
		<div className='container_pages container_exp'>
			{/* <h1 className='title_exp'>Working Experience</h1> */}
			<div className='exp_li'>
				{ExpItems.map((item, index) => {
					const background = {
						backgroundImage: `url(../images/exp/${item.img})`,
					};
					return item.cert != null ? (
						<span className='experience' key={index}>
							<div className='loc-img' style={background}></div>
							<span className='exp_text'>
								<h3 className='work_title'>D{item.title}</h3>
								<a target='_blank' href={item.link}>
									{item.loc}
								</a>
								<p className='work_period'>{item.period}</p>
							</span>
						</span>
					) : (
						<span className='experience' key={index}>
							<div className='loc-img' style={background}></div>
							<span className='exp_text'>
								<h3 className='work_title'>{item.title}</h3>
								<a target='_blank' href={item.link}>
									{item.loc}
								</a>
								<p className='work_period'>{item.period}</p>
							</span>
						</span>
					);
				})}
			</div>
		</div>
	);
};

export default Experience;
