/** @format */

import React from 'react';
import '../App.css';
import { SupplierList, SustainPoints, SustainPhotos } from '../TextInfo';
import SupBlock from '../components/SupBlock';

const Suppliers = () => {
	return (
		<div className='container_page'>
			<h1 className='title_exp'>Doing the right thing is important for our future generations –</h1>
			<span className="sustain_filler">
				<div className='filler-text'>
					<p className='sustainability_text'>Under that perspective from chef Yannick’s integrity of food and resources, introducing commercial players within the supply chain is key to reduce our carbon foot print, the over-fishery, utilization of plastics in the food and catering industry.</p>
					<ul className='work_li'>
						{SustainPoints.map( ( item, index ) => {
							return (
								<li key={index} className='sustainability_text'>{item}</li>
							);
						} )}
					</ul>
				</div>
				<div className='filler-photos'>
					{SustainPhotos.map( ( item, index ) => {
						let background = `../images/sustainPhotos2/${item}`
						return (
							<span key={index}>
								<img src={background} alt="" />
							</span>
						);
					} )}
				</div>
			</span>
			<h3 className='title_exp'>Past events supporting suppliers with</h3>
			<div className='container_exp'>
				<span className='exp_list supplier'>
					{SupplierList.map( ( item, index ) => {
						let background = `../images/suppliersLogo/${item.logo}`
						return (
							<span key={index}>
								<img src={background} alt="" />
							</span>
						);
					} )}
				</span>
			</div>

		</div>
	);
};

export default Suppliers;
