import React, { useRef, useState } from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";
import { SustainableImages } from "../TextInfo";
import { HealthList } from "../TextInfo";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";
import "../App.css";

// import required modules
import { Autoplay, Pagination, Navigation } from "swiper";

const Swipe = ( props ) => {
    const autoplayVar = {}

    props.for == "sustain" ? autoplayVar = {
        delay: 2500,
        disableOnInteraction: false,

    } : {}


    return (
        <>
            <Swiper
                spaceBetween={70}
                centeredSlides={true}
                autoplay={{
                    delay: 2500,
                    disableOnInteraction: false,
                }}
                pagination={{
                    clickable: false,
                }}
                navigation={true}
                modules={[Autoplay, Pagination, Navigation]}
                className="mySwiper"
            >
                {props.for == "sustain" ? (
                    SustainableImages.map( ( imgpath, idx ) => {
                        const img = `../images/sustainPhotos/${imgpath}`;

                        return (
                            <SwiperSlide key={`${idx}`} className={"swiper-slide"}>
                                <img className={"swiper-img-sustain"} src={img} alt="event images" />
                            </SwiperSlide>
                        );
                    } )
                ) : (
                    props.idx.map( ( imgpath, idx ) => {
                        const img = `../images/healthPhotos/${imgpath}`;

                        return (
                            <SwiperSlide key={`${idx}`} className={"swiper-slide"}>
                                <img className={"swiper-img-health"} src={img} alt="event images" />
                            </SwiperSlide>
                        );
                    } )
                )}
            </Swiper>
        </>
    );
}
export default Swipe;
