/** @format */

import React from 'react';
import { ShowcaseDishes } from './TextInfo';
import bck from '../images/bck.jpg';

const Gallery = () => {
	return (
		<div className='container_page'>
			<span className='gallery'>
				{ShowcaseDishes.map((item, index) => {
					return item.type == 'Showcased Dishes' ? (
						<span className='gallery-item'>
							<p>{item.type}</p>
						</span>
					) : (
						<span
							className='gallery-item'
							style={{ backgroundImage: 'url(../images/bck.jpg )' }}>
							{/* <img src={bck} alt='background' /> */}
							<p>{item.type}</p>
						</span>
					);
				})}
			</span>
		</div>
	);
};

export default Gallery;
