import React from 'react'

const EventsBlock = ( item ) => {
    const logo = {
        backgroundImage: `url(../images/event/logo/${item.item.img})`,
    };
    const background = {
        backgroundImage: `url(../images/event/pictures/${item.item.img})`,
    };


    return (
        item.item.type == "day" ? (
            <span className='exp_block'>
                <div className='exp_location_img' style={logo}></div>
                <span className='exp_header'>
                    <h3 className='exp_position'>{item.item.title}</h3>
                    <a target='_blank' rel='noreferrer' href={item.item.link}>
                        {item.item.loc}
                    </a>
                    <p className='exp_period'>{item.item.period}</p>
                </span>
            </span>
        ) : (
            null
        )
    );
}

export default EventsBlock
