import React from 'react'
import '../App.css';

const EventsBlock = ( item ) => {
    const logo = "";
    const background = '';
    const desc = null;

    if ( item.item.logo != "" || item.item.logo != null ) {
        logo = {
            backgroundImage: `url(../images/event/logo/${item.item.logo})`,
        }
    }
    if ( item.item.img != "" || item.item.img != null )
        background = `../images/event/pictures/${item.item.img}`
    if ( item.item.desc != "" || item.item.desc != null )
        desc = <div className='event_desc'>
            <p >{item.item.desc}</p>
        </div>

    return (
        item.item.type == "day" ? (
            <span className='exp_block'>
                <div className='event_logo' style={logo}></div>
                <span className='exp_header'>
                    <h3 className='exp_position'>{item.item.title}</h3>
                    {desc}
                    <ul className='work_li'>
                        {item.item.list.map( ( item, index ) => {
                            return <li key={index}>{item}</li>;
                        } )}
                    </ul>
                    <img className='event_action_picture' src={background} alt="" />
                </span>
            </span>
        ) : (
            item.item.type == "img" ? (
                <span className='exp_block'>
                    <div className='event_logo' style={logo}></div>
                    <span className='exp_header'>
                        <h3 className='exp_position'>{item.item.title}</h3>
                        <div className='event_desc'>
                            <p >{item.item.desc}</p>
                        </div>
                        <ul className='work_li'>
                            {item.item.list.map( ( item, index ) => {
                                return <li key={index}>{item}</li>;
                            } )}
                        </ul>
                        <img className='event_action_picture' src={background} alt="" />
                    </span>
                </span>
            ) : (
                null
            )
        )
    );
}

export default EventsBlock
