/** @format */

import React from 'react';
import { ShowcaseDishes } from '../TextInfo';
import bck from '../images/bck.png';
import border from '../images/border.png';

const Gallery = () => {
	return (
		<div className='container_page'>
			<span className='gallery'>
				{ShowcaseDishes.map( ( item, index ) => {
					// const background = { backgroundImage: `url(${bck})` };
					return item.type === 'Yannick Guillemot' ? (
						<span className='gallery-title' key={index}>
							<h3>{item.type}</h3>
						</span>
					) : item.theme !== null ? (
						// style={background}
						<span className='gallery-item' key={index}>
							<h3>{item.type}</h3>
							<img src={border} alt='' />
							<h4>{item.theme}</h4>
						</span>
					) : (
						<span className='gallery-item' key={index}>
							<h3>{item.type}</h3>
						</span>
					);
				} )}
			</span>
		</div>
	);
};

export default Gallery;
