import { React, useState } from 'react';
import { MenuList } from '../TextInfo';
import '../App.css';
import Swipe from '../components/Swipe';
import { MdArrowRight, MdArrowDropDown } from 'react-icons/md';
import HorizontalScroll from '../components/HorizontalScroll';
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";
// Import Swiper styles
import img from "../images/menus/cafe-des.png";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

const Events = () => {
    const [active, setActive] = useState( 1 );
    const dropdown = ( index ) => {
        if ( active !== index )
            setActive( index );
        else
            setActive( 1000 );
    };
    return (
        <span className="container_pages container_exp">
            <span className="menu_list">
                {MenuList.map( ( item, index ) => {
                    return <span className="menu_cat">
                        <span className='exp_block exp_cert_block' onClick={() => dropdown( index )} key={index}>

                            <span className='exp_header'>
                                <span className='exp_toggle_title'>
                                    <h3 className='exp_position'>{item.category}</h3>
                                    <span className='toggle_icon'>
                                        {active === index ? (
                                            <MdArrowDropDown className='toggle-arr' />
                                        ) : (
                                            <MdArrowRight className='toggle-arr' />
                                        )}
                                    </span>
                                </span>
                            </span>
                        </span>
                        {item.isSet ? (
                            // item.set.map( ( dish, index ) => {
                            //     return <span className="menu_set">
                            //         <h3>{dish.name}</h3>
                            //         {dish.desc.map( ( dishDesc, index ) => {
                            //             const dishImg = {
                            //                 backgroundImage: `url(../images/menus/cafe-des.png)`,
                            //             };
                            //             return <span className="menu_dish">
                            //                 <div className="menu_dish_img" style={dishImg}></div>
                            //                 <p>{dishDesc}</p>
                            //             </span>
                            //         } )}
                            //     </span>
                            // } )
                            null
                        ) : (
                            <Swiper
                                slidesPerView={3}
                                spaceBetween={30}
                                slidesPerGroup={3}
                                loop={true}
                                loopFillGroupWithBlank={true}
                                pagination={{
                                    clickable: true,
                                }}
                                navigation={true}
                                modules={[Pagination, Navigation]}
                                className="mySwiper"
                            >
                                <SwiperSlide>Slide 1</SwiperSlide>
                                <SwiperSlide>Slide 2</SwiperSlide>
                                <SwiperSlide>Slide 3</SwiperSlide>
                                <SwiperSlide>Slide 4</SwiperSlide>
                                <SwiperSlide>Slide 5</SwiperSlide>
                                <SwiperSlide>Slide 6</SwiperSlide>
                                <SwiperSlide>Slide 7</SwiperSlide>
                                <SwiperSlide>Slide 8</SwiperSlide>
                                <SwiperSlide>Slide 9</SwiperSlide>
                            </Swiper>
                        )}
                    </span>
                } )}
            </span>
        </span>
    );
}
export default Events
