/** @format */

import React, { useState } from 'react';
import '../App.css';
import { Link } from 'react-router-dom';
import { ExpItems } from '../TextInfo';
import { MdArrowRight, MdArrowDropDown } from 'react-icons/md';

const ExpContent = (index, title, link, loc, period, cert, background) => {
	const [active, setActive] = useState(false);
	return (
		<span>
			<span
				className='experience'
				// key={index}
				onClick={(e) => setActive(!active)}>
				<div className='loc-img' style={background}></div>
				<span className='exp_text'>
					<span className='cert_work_title'>
						<h3 className='work_title'>{title}</h3>
						<span className='toggle'>
							<MdArrowDropDown className='toggle-arr' />
						</span>
					</span>
					<a target='_blank' href={link}>
						{loc}
					</a>
					<p className='work_period'>{period}</p>
				</span>
			</span>
			{active && (
				<span className='exp_content'>
					{cert.map((item, index) => {
						const certLink = `/../images/cert/${item}`;
						const cert = {
							backgroundImage: `url(${certLink})`,
						};
						return (
							<Link to={certLink}>
								<div className='exp_cert' style={cert}></div>
							</Link>
						);
					})}
				</span>
			)}
		</span>
	);
};

export default ExpContent;
