import { useRef, useState } from "react";
import "../App.css";
import {SustainableImages} from "../TextInfo";

const Carousel = ({ images }) => {

  const carousel = useRef();
  const [count, setCount] = useState(0);

  const incrementCarousel = delta => {
    if (!carousel.current) return;

    const width = carousel.current.offsetWidth;

    if (count + delta > SustainableImages.length - 1) {
      setCount(0);
      carousel.current.scrollTo(0, 0);
      return;
    } else if (count + delta < 0) {
      setCount(SustainableImages.length - 1);
      console.log(width, carousel.current.scrollLeft);
      carousel.current.scrollTo(width * SustainableImages.length - 1, 0);
      return;
    }

    carousel.current.scrollTo(
      carousel.current.scrollLeft + width * delta,
      0
    );
    setCount(c => c + delta);
  };

  return (
    <div className="carousel-container">
      <div
        className="carousel-btn left-btn"
        onClick={() => incrementCarousel(-1)}
      />
      <div
        className="carousel-btn right-btn"
        onClick={() => incrementCarousel(1)}
      />
      <div className="carousel" ref={carousel}>
        {SustainableImages.map((img, idx) => {
						return (<div
            key={`${idx}`}
            className={
              idx === count ? "carousel-item active" : "carousel-item"
            }
          >
            <img src={img.src} alt="event images" />
          </div>
						);
					})}
      </div>
    </div>
  );
};

export default Carousel;
