import React, { useRef, useState } from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";
// Import Swiper styles
import img from "../images/menus/cafe-des.png";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";
import "../App.css";

// import required modules
import { Pagination, Navigation } from "swiper";

const HorizontalScroll = ( props ) => {
    let [mywindow, setMywindow] = React.useState( {} )
    var resizeId;
    React.useEffect( () => {
        window.addEventListener( "resize", function () {
            clearTimeout( resizeId );
            resizeId = setTimeout( doneResizing, 500 );

        } )
        function doneResizing() {
            console.log( "window is ", window.innerHeight, window.innerWidth )
            setMywindow( { height: window.innerHeight, width: window.innerWidth } )
        }

    }, [mywindow] )

    return (
        <>
            <Swiper
                slidesPerView={mywindow.width && mywindow.width < 700 ? 2 : 1}
                spaceBetween={50}
                slidesPerGroup={3}
                loop={true}
                loopFillGroupWithBlank={true}
                pagination={{
                    clickable: true,
                }}
                navigation={true}
                modules={[Pagination, Navigation]}
                className="mySwiper"
            >
                {props.list.map( ( dish, index ) => {
                    const dishImg = {
                        backgroundImage: `url(../images/menus/cafe-des.png)`,
                    };
                    return <SwiperSlide>
                        <span className="menu_dish">
                            <div className="menu_dish_img" style={dishImg}></div>
                            <h5>{dish.name}</h5>
                            {dish.desc != null ?
                                dish.desc.map( ( dishDesc, index ) => {
                                    return <p className="menu_dish_desc">{dishDesc}</p>
                                } )
                                : null}
                        </span>
                    </SwiperSlide>
                } )
                }
            </Swiper>
        </>
    );
}

export default HorizontalScroll;
